import React, { useState } from 'react';
import './App.css';
import Person from './Person/Person';

const App = props => {
  const [personsState, setPersonsState] = useState({
    persons: [
      {name: 'Max', age: 28 },
      {name: 'dunga', age: 31 },
      {name: 'Kaboul', age: 24 }
   ],
   otherState: 'some text here'

   });
   console.log(personsState)

   const switchNameHandler = () => {
      // console.log("was clicked !");
      // this.state.persons[0].name = 'Jkula'
      setPersonsState({
        persons: [
          {name: 'Maximilian', age: 28 },
          {name: 'dunga', age: 31 },
          {name: 'Kaboul', age: 24 }
        ],
      });
    }

    return (
         <div className="App">
            <h1>Hi, I'm a React App</h1>
            <p>This is working</p>
            <button onClick={switchNameHandler}>Switch Name</button>
            <Person name={personsState.persons[0].name} age={ personsState.persons[0].age }>My hobbies is Playing guitar</Person>
            <Person name={personsState.persons[1].name} age={ personsState.persons[1].age }/>
            <Person name={personsState.persons[2].name} age={ personsState.persons[2].age } />

         </div>
    )
    //  return React.createElement('div', { className: 'App'}, 
    //                     React.createElement('h1', null, 'I\'m here now'))

}

export default App;
